frappe.ui.form.on('Quotation', {
	refresh(frm){
	 show_hide(frm)

	},
	onload(frm){
	    standard_query(frm);
	    show_hide(frm)
	    if(frm.doc.__islocal){
	        frm.set_value("taxes_and_charges", "");
	        frm.doc.taxes = [];
	        frm.refresh_field("taxes");
	    }
	},
	setup(frm){
	    standard_query(frm)
	    show_hide(frm)

	},
	quotation_to(frm){
	    standard_query(frm)
	},
	scan_barcode (frm) {
		//console.log(frm)
		let scan_barcode_field = frm.fields_dict["scan_barcode"];

		let show_description = function (idx, exist = null) {
			if (exist) {
				scan_barcode_field.set_new_description(__('Row #{0}: Qty increased by 1', [idx]));
			} else {
				scan_barcode_field.set_new_description(__('Row #{0}: Item added', [idx]));
			}
		}

		if (frm.doc.scan_barcode) {
			frappe.call({
				method: "erpnext.selling.page.point_of_sale.point_of_sale.search_serial_or_batch_or_barcode_number",
				args: { search_value: frm.doc.scan_barcode }
			}).then(r => {
				const data = r && r.message;
				console.log(data)
				if (!data || Object.keys(data).length === 0) {
					scan_barcode_field.set_new_description(__('Cannot find Item with this barcode'));
					return;
				}

				let cur_grid = frm.fields_dict.items.grid;

				let row_to_modify = null;
				if (frm.doc.items) {
					const existing_item_row = frm.doc.items.find(d => d.item_code === data.item_code);
					const blank_item_row = frm.doc.items.find(d => !d.item_code);

					if (existing_item_row) {
						row_to_modify = existing_item_row;
					} else if (blank_item_row) {
						row_to_modify = blank_item_row;
					}
				}

				if (!row_to_modify) {
					// add new row
					row_to_modify = frappe.model.add_child(frm.doc, cur_grid.doctype, 'items' ,1);
				}

				show_description(row_to_modify.idx, row_to_modify.item_code);

				frm.from_barcode = true;
				frappe.model.set_value(row_to_modify.doctype, row_to_modify.name, {
					item_code: data.item_code,
					qty: (row_to_modify.qty || 0) + 1
				});

				['serial_no', 'batch_no', 'barcode'].forEach(field => {
					if (data[field] && frappe.meta.has_field(row_to_modify.doctype, field)) {
						frappe.model.set_value(row_to_modify.doctype,
							row_to_modify.name, field, data[field]);
					}
				});

				scan_barcode_field.set_value('');
				cur_frm.refresh_field("items");
                                var target= cur_frm.doc.items.filter(function (item) {
                                   return item.item_code=== data.item_code;
                                });
                                target.barcode = data.barcode;
                                cur_frm.refresh_field("items");
				frm.refresh_field("item_code");
			});
		}

	},
})

var standard_query = function(frm){
    if(frm.doc.quotation_to == "Customer"){
	    frm.set_query("party_name", function() {
			return {
              query: "erpnext.controllers.queries.customer_query"
            }
		});
    }
}










var show_hide = function(frm){
    frm.toggle_display("payment_schedule_section", false);
    frm.toggle_display("print_settings", false);

}

