css:
.table > tbody > tr > td {
    border-top: none;
}

.print-format th{
    background-color: #FFF !important;
}

.store_credit_header_pos .border-bottom{
    border-bottom: 1px dashed black;
    padding-bottom: 5px;
    width: 100%;
    margin-bottom: 20px;
    font-size: 17px;
}

.store_credit_header_pos .border-top{
    border-top: 1px dashed black;
}

.store_credit_header_pos .print-format td{
    width: 1%;
}

.store_credit_header_pos .text-center{
    text-align: center;
}


.store_credit_header_pos td{
    padding: 0px 5px !important;
    line-height: 130% !important;
}

.store_credit_header_pos.table td{
    padding: 5px !important;
}

.store_credit_header_pos h4{
    font-weight: 800;
    font-size: 14px;
}

.store-credit-h4{
    font-weight: 800;
    font-size: 18px;
    margin-bottom: 3px;
}

.store_credit_header_pos .tr-label{
    width: 120px;
}

.print-format .contact_info{
    padding: 0px !important;
    margin: 0px !important;
    line-height: 140%;
    text-align: center;
    font-size: .9em;
}
